function _startsWith(a, b) return string.sub(a, 1, #b) == b end

local _nbSamplePoints
local _squareSize = 250
local _phi = 0.5 * (-1.0 + math.sqrt(5.0))
local _angleRange = math.rad(45.0)
local _anglePrecision = math.rad(2.0)
local _halfDiagonal = math.sqrt(_squareSize * _squareSize + _squareSize * _squareSize) * 0.5
local _origin = { x = 0, y = 0 }

local function getDistance(a, b)

    local dx = b.x - a.x
    local dy = b.y - a.y

    return math.sqrt(dx * dx + dy * dy)
end

local Stroke = {}
function Stroke.new(rotate, points, name)
    assert(type(rotate) == "boolean", "rotate is not a boolean")
    assert(type(points) == "table", "points is not a table")

    local this = {}

    this.rotate = rotate
    this.points = points
    this.name = name
    Stroke.processStroke(this)

    return setmetatable(this, {__index = Stroke})
end

Stroke.processStroke = function(this)

    this.points = Stroke.resample(this)
    Stroke.setCentroid(this)
    if this.rotate then this.points = Stroke.rotateBy(this, -Stroke.indicativeAngle(this)) end
    this.points = Stroke.scaleToSquare(this)
    Stroke.setCentroid(this)
    this.points = Stroke.translateToOrigin(this)
    
    return this
end

Stroke.resample = function(this)

    local localDistance, q
    local interval = Stroke.strokeLength(this) / (_nbSamplePoints - 1)
    local distance = 0.0
    local newPoints = {this.points[1]}

    local i = 2
    while i <= #this.points do
        localDistance = getDistance(this.points[i - 1], this.points[i])

        if distance + localDistance >= interval then
            q = {
                x = this.points[i - 1].x + ((interval - distance) / localDistance) * (this.points[i].x - this.points[i - 1].x),
                y = this.points[i - 1].y + ((interval - distance) / localDistance) * (this.points[i].y - this.points[i - 1].y)
            }

            table.insert(newPoints, q)
            table.insert(this.points, i, q)
            distance = 0.0
        else
            distance = distance + localDistance
        end

        i = i + 1
    end
    
    if #newPoints == _nbSamplePoints - 1 then
        table.insert(newPoints, this.points[#this.points])
    end
    
    return newPoints
end

Stroke.rotateBy = function(this, angle)

    local point
    local cos = math.cos(angle)
    local sin = math.sin(angle)
    local newPoints = {}
    
    for i = 1, #this.points do
        point = this.points[i]

        table.insert(newPoints, {
            x = (point.x - this.c.x) * cos - (point.y - this.c.y) * sin + this.c.x,
            y = (point.x - this.c.x) * sin + (point.y - this.c.y) * cos + this.c.y
        })
    end

    return newPoints
end

Stroke.scaleToSquare = function(this)

    local point
    local newPoints = {}
    local box = {
        minX = math.huge,
        maxX = -math.huge,
        minY = math.huge,
        maxY = -math.huge
    }

    for i = 1, #this.points do
        point = this.points[i]
        
        box.minX = math.min(box.minX, point.x)
        box.minY = math.min(box.minY, point.y)
        box.maxX = math.max(box.maxX, point.x)
        box.maxY = math.max(box.maxY, point.y)
    end

    box.width = box.maxX - box.minX
    box.height = box.maxY - box.minY

    for i = 1, #this.points do
        point = this.points[i]

        table.insert(newPoints, {
            x = point.x * (_squareSize / box.width),
            y = point.y * (_squareSize / box.height)
        })
    end

    return newPoints
end

Stroke.translateToOrigin = function(this, points)

    local point
    local newPoints = {}
    
    for i = 1, #this.points do
        point = this.points[i]
    
        table.insert(newPoints, {
            x = point.x + _origin.x - this.c.x,
            y = point.y + _origin.y - this.c.y
        })
    end

    return newPoints
end

Stroke.setCentroid = function(this)
    
    local point
    this.c = {
        x = 0.0,
        y = 0.0
    }

    for i = 1, #this.points do
        point = this.points[i]

        this.c.x = this.c.x + point.x
        this.c.y = this.c.y + point.y
    end

    this.c.x = this.c.x / #this.points
    this.c.y = this.c.y / #this.points
    
    return this
end

Stroke.indicativeAngle = function(this)

    return math.atan2(this.c.y - this.points[1].y, this.c.x - this.points[1].x)
end

Stroke.strokeLength = function(this)
    
    local d = 0.0

    for i = 2, #this.points do
        d = d + getDistance(this.points[i - 1], this.points[i])
    end

    return d
end

Stroke.distanceAtBestAngle = function(this, pattern)
    
    local a = -_angleRange
    local b = _angleRange
    local x1 = _phi * a + (1.0 - _phi) * b
    local f1 = Stroke.distanceAtAngle(this, pattern, x1)
    local x2 = (1.0 - _phi) * a + _phi * b
    local f2 = Stroke.distanceAtAngle(this, pattern, x2)

    while math.abs(b - a) > _anglePrecision do
        
        if f1 < f2 then
            b = x2
            x2 = x1
            f2 = f1
            x1 = _phi * a + (1.0 - _phi) * b
            f1 = Stroke.distanceAtAngle(this, pattern, x1)
        else
            a = x1
            x1 = x2
            f1 = f2
            x2 = (1.0 - _phi) * a + _phi * b
            f2 = Stroke.distanceAtAngle(this, pattern, x2)
        end
    end

    return math.min(f1, f2)
end

Stroke.distanceAtAngle = function(this, pattern, angle)

    local strokePoints = Stroke.rotateBy(this, angle)
    local patternPoints = pattern.points
    local d = 0.0

    for i = 1, #strokePoints do
        d = d + getDistance(strokePoints[i], patternPoints[i])
    end

    return d / #strokePoints
end

function ShapeDetector.new(patterns, options)
    assert(type(patterns) == "table", "patterns is not a table")

    local this = {}

    options = options or {}
    this.rotatable = type(options.rotatable) == "nil" and true or options.rotatable
    this.threshold = options.threshold or 0.9
    _nbSamplePoints = options.nbSamplePoints or 64

    this.patterns = {}

    for i = 1, #patterns do
        ShapeDetector.learn(this, patterns[i].name, patterns[i].points)
    end

    return setmetatable(this, {__index = ShapeDetector})
end

