ShapeDetector.spot = function(this, points, patternName)
    assert(type(points) == "table", "points is not a table")

    if not patternName then patternName = "" end

    local distance, pattern, score
    local stroke = Stroke.new(this.rotatable, points)
    local bestDistance = math.huge
    local bestPattern = nil
    local bestScore = 0

    for i = 1, #this.patterns do
        pattern = this.patterns[i]

        if _startsWith(pattern.name, patternName) then
            distance = Stroke.distanceAtBestAngle(stroke, pattern)
            score = 1.0 - distance / _halfDiagonal

            if distance < bestDistance and score > this.threshold then
                bestDistance = distance
                bestPattern = pattern.name
                bestScore = score
            end
        end
    end

    return { pattern = bestPattern, score = bestScore }
end

ShapeDetector.learn = function(this, name, points)
    assert(type(name) == "string", "name is not a string")
    assert(type(points) == "table", "points is not a table")

    return table.insert(this.patterns, Stroke.new(this.rotatable, points, name))
end

ShapeDetector.Stroke = Stroke

return ShapeDetector